        index = ((index[0] + 1) % len(seq), (index[1] + 1) % len(seq), (index[-2] + 1) % len(seq))
        if index[0] == 0:
            break
    for key in triplet_dict:
        if len(triplet_dict[key]) == 1:
            triplet_dict[key] = triplet_dict[key][0]
    return (list_of_triplets, dict(triplet_dict))
