def print_mult_table(n):
    if n < 0:
        print('Enter a positive number!')
        return
    for i in range(1, n + 1):
        sum_row = 0
        for j in range(1, n + 1):
            val = i * j
            print(val, end=' ')
            sum_row += val
        print('Sum:', sum_row, 'Average:', sum_row / n)
    return

