import heapq

def kth_smallest_even(nums, k):
    even_nums = [num for num in nums if num % 2 == 0]
    if k >= len(even_nums):
        return None
    heapq.heapify(even_nums)
    result = None
    for _ in range(k):
        result = heapq.heappop(even_nums)
    return result

