def nested_common(list1, list2):

    def flatten(lis):
        """"""
        for item in lis:
            if isinstance(item, list):
                for x in flatten(item):
                    yield x
            else:
                yield item
    list1_flat = set(flatten(list1))
    list2_flat = set(flatten(list2))
    common = list1_flat.intersection(list2_flat)
    return list(common)
