def calculate_vowels(sentence):
    vowels = ''
    words_dict = {}
    words = sentence.split()
    for word in words:
        word_lowered = word.lower()
        vowels_dict = {vowel: word_lowered.count(vowel) for vowel in vowels}
        words_dict[word] = vowels_dict
    return words_dict
