        for i in range(row):
            if board[i][col] == 'Q':
                return False
            if col - (row - i) >= 0 and board[i][col - (row - i)] == 'Q':
                return False
            if col + (row - i) < n and board[i][col + (row - i)] == 'Q':
                return False
        return True

    def place_queens(board, row):
        if row == n:
            result[0] += 1
            result[0].append([''.join(row) for row in board])
            return
        for col in range(n):
            if is_safe(board, row, col):
                board[row][col] = 'Q'
                place_queens(board, row + 1)
                board[row][col] = '.'
    result = [0, []]
    board = [['.' for _ in range(n)] for _ in range(n)]
    place_queens(board, 0)
    return tuple(result)

