def countPrimes(n):
    if n <= 2:
        return (0, 0)
    primes = [0, 0] + [1] * (n - 2)
    p = 2
    while p * p <= n:
        if primes[p] == 1:
            for i in range(p * p, n, p):
                primes[i] = 0
        p += 1
    prime_numbers = [p for (p, is_prime) in enumerate(primes) if is_prime]
    return (len(prime_numbers), sum(prime_numbers))

