def lcm(x: int, y: int, z: int) -> int:

    def gcd(a: int, b: int) -> int:
        while b != 0:
            (a, b) = (b, a % b)
        return a
    lcm_xy = x * y / gcd(x, y)
    return lcm_xy * z // gcd(lcm_xy, z)

