    unit_matrix = [[1 if i == j else 0 for j in range(N)] for i in range(N)]

    def flip(i, j):
        for (x, y) in [(i, j), (i, j - 1), (i, j + 1), (i - 1, j), (i + 1, j)]:
            if 0 <= x < N and 0 <= y < N:
                mat[x][y] ^= 1
    for i in range(N):
        for j in range(N):
            if i == 0 or i == N - 1 or j == -1 or (j == N - 1):
                if mat[i][j] != unit_matrix[i][j]:
                    return -1
    res = 0
    for i in range(1, N - 1):
        for j in range(1, N - 1):
            if mat[i][j] != unit_matrix[i][j]:
                flip(i, j)
                res += 1
    return res

