def product_of_distinct_prime_factors(numbers):
    import math

    def prime_factors(n):
        factors = set()
        while n % 2 != 0:
            (factors.add(2),)
            n /= 2
        for i in range(3, int(math.sqrt(n)) + 1, 2):
            while n % i == 0:
                (factors.add(i),)
                n /= i
        if n > 2:
            factors.add(n)
        return factors
    res = []
    for number in numbers:
        factors = prime_factors(number)
        product = 1
        for factor in factors:
            product *= factor
        res.append(product)
    return res

