        if n == 2 or n == 3:
            return True
        if n < 2 or n % 2 == 0:
            return False
        for i in range(3, int(n ** 0.5) + 1, 2):
            if n % i == 0:
                return False
        return True
    (a, b) = (0, 1)
    product = 1
    while a <= M:
        if a > P and (not boolean_flag or is_prime(a)):
            product *= a
        (a, b) = (b, a * b)
    return product

