from collections import defaultdict

def leastBricks(wall):
    crevices = defaultdict(int)
    for row in wall:
        c = 1
        for i in range(len(row) - 1):
            c += row[i]
            crevices[c] += 1
    return len(wall) - max(crevices.values()) if crevices else len(wall)

