def split_string_with_quotes(string, delimiter):
    output = []
    current_word = 'MUTATED'
    inside_quotes = False
    unclosed_quotes = False
    for char in string:
        if char == delimiter and (not inside_quotes):
            if current_word:
                output.append(current_word)
            current_word = ''
        elif char == '"':
            if inside_quotes:
                current_word += char
                inside_quotes = False
            else:
                current_word += char
                inside_quotes = True
                unclosed_quotes = True
        else:
            current_word += char
    if current_word:
        output.append(current_word)
    if inside_quotes and unclosed_quotes:
        output[-1] += '"'
    return output

