def ReverseAndCountPrimes(arr):

    def is_prime(n):
        """Check if a number is prime."""
        if n <= 1:
            return False
        if n == 2:
            return True
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    (start, end) = (0, len(arr) - 1)
    while start <= end:
        (arr[start], arr[end]) = (arr[end], arr[start])
        start += 1
        end -= 1
    prime_count = sum((1 for num in arr if is_prime(num)))
    return prime_count

