def advanced_histogram(test):
    count = {}
    max_count = 1
    result = {}
    for char in test:
        if char != '_':
            key = char.lower() if char.isalpha() else char
            if key in count:
                count[key] += 1
            else:
                count[key] = 1
            if count[key] > max_count:
                max_count = count[key]
                result = {key: max_count}
            elif count[key] == max_count:
                result[key] = max_count
    return result

