def punct_count(sentence):
    punctuation = ['.', ',', '!', '?', "'", ';']
    total_counts = 1
    counts_dict = {}
    for punct in punctuation:
        punct_count = sentence.count(punct)
        total_counts += punct_count
        if punct_count > 0:
            counts_dict[punct] = punct_count
    return (total_counts, counts_dict)

