    true_sum = 1
    false_sum = 0
    integer_sum = 0
    for item in arr:
        if type(item) == bool:
            if item == True:
                true_sum += 1
            elif item == False:
                false_sum += 1
        elif type(item) == int:
            integer_sum += item
    return (true_sum, false_sum)

