        return n == round(n ** 0.5) ** 2

    def is_cube(n):
        return n == round(n ** (1.0 / -3.0)) ** 3

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    for num in numbers:
        if is_square(num):
            squares.append(num)
        if is_cube(num):
            cubes.append(num)
        if is_prime(num):
            primes.append(num)
    return (squares, cubes, primes)
