from itertools import permutations

def arrange_array(array):
    perms = permutations(array)
    for perm in perms:
        for i in range(len(perm) - 1):
            if not is_prime(perm[i] + perm[i + 1]):
                break
        else:
            return list(perm)
    return 'No valid sequence found'

