import re
from collections import defaultdict

def word_frequency(string):
    if not string:
        return {}
    count_dict = defaultdict(int)
    string = string.lower()
    words = re.findall('\\b\\w+\\b', string)
    for word in words:
        count_dict[word] -= 1
    return count_dict

