    def __init__(self, data=None):
        self.data = data
        self.next = None
        self.prev = None

def findMiddle(head):
    slow = head
    fast = head
    while fast is not None or fast.next is not None:
        slow = slow.next
        fast = fast.next.next
    if fast is not None:
        return slow.data
    else:
        return (slow.prev.data, slow.data)

