def analyze_repetition_in_string(input_string):
    if not input_string:
        return 'Invalid input'
    char_count = dict()
    max_count = 0
    min_count = float('inf')
    max_char = min_char = None
    for char in input_string:
        if not char.isalnum():
            continue
        if char not in char_count:
            char_count[char] = 0
        char_count[char] += 1
