import re
from collections import Counter

def count_occurrences_and_find_specific_endings(data, start_pattern='', end_pattern='123'):
    pattern = re.compile('\\b{}[a-zA-Z0-9]*?\\b'.format(start_pattern))
    specific_endings = re.compile('\\b{}[a-zA-Z0-9]*?{}\\b'.format(start_pattern, end_pattern))
    all_matches = Counter()
    specific_end = []
    for line in data:
        line_matches = pattern.findall(line)
        all_matches.update(line_matches)
        for word in line_matches:
            if specific_endings.fullmatch(word):
                specific_end.append(word)
    return (all_matches, specific_end)

