def hex_to_bin(hex_list):
    bin_list = []
    error_list = []
    for i in hex_list:
        try:
            bin_list.append(bin(int(i, 16))[2:].zfill(5))
        except ValueError:
            error_list.append(f'Error: Invalid hexadecimal number {i}')
    return (bin_list, error_list)

