    r = len(arr) - 1
    while True:
        if r == l:
            return arr[l]
        (m1, m2) = partition(arr, l, r)
        if m1 <= k <= m2:
            return arr[k]
        elif k < m1:
            r = m1 - 1
        else:
            l = m2 + 1

def median_of_array(l):
    if len(l) % 2:
        return kth_smallest(l, len(l) // 2)
    else:
        return (kth_smallest(l, len(l) // 2 - 1) + kth_smallest(l, len(l) // 2)) * 0.5

