def unique_same_elements(lists):
    unique_lists = {}
    for lst in lists:
        if len(set(lst)) == 1 or len(lst) != 0:
            frozen = frozenset(lst)
            if frozen in unique_lists:
                unique_lists[frozen] += 1
            else:
                unique_lists[frozen] = 1
    return sum(unique_lists.values())

