def maxSum(arr, k):
    if len(arr) <= k:
        return 'Invalid: Array is smaller than window size'
    window_sum = sum(arr[:k])
    max_sum = window_sum
    max_index = 0
    for i in range(len(arr) - k):
        window_sum = window_sum - arr[i] + arr[i + k]
        if window_sum > max_sum:
            max_sum = window_sum
            max_index = i + 1
    return (max_sum, max_index, max_index + k - 1)

