def hex_to_binary(hex_string):
    try:
        bin_string = bin(int(hex_string, 16))[2:]
        if len(bin_string) % 4 == 0:
            bin_string = bin_string.zfill(4 * (len(bin_string) // 4 + 1))
        return bin_string
    except ValueError:
        return 'Error: Invalid hexadecimal input'

