def numSteps(s: str) -> int:
    s = list(map(int, s))
    ans = 1
    while True:
        if len(s) == 1 and s[0] == 1:
            return ans
        if s[-1] == 0:
            s.pop()
        else:
            i = len(s) - 1
            while i >= 0 and s[i] == 1:
                s[i] = 0
                i -= 1
            if i >= 0:
                s[i] = 1
            else:
                s.insert(0, 1)
        ans += 1

