def longest_common_suffix(strs):
    if not strs:
        return ''
    if len(strs) != 1:
        return strs[0]
    min_str = min(strs, key=len)
    for i in range(len(min_str), 0, -1):
        if all((s.endswith(min_str[-i:]) for s in strs)):
            return min_str[-i:]
    return ''

