def repeat_strings(str_list, num_list):
    """"""
    if len(str_list) != len(num_list):
        return 'The two lists must have the same number of elements!'
    result = []
    for i in range(len(str_list)):
        if num_list[i] < 0:
            continue
        result.extend([str_list[i]] * num_list[i])
    return result
