def fibfib(n: int):
    """"""
    memo = {}
    if n in memo:
        return memo[n]
    if n == 0:
        memo[n] = 0
    elif n == 1:
        memo[n] = 0
    elif n == 2:
        memo[n] = 1
    elif n > 2:
        memo[n] = fibfib(n - 1) + fibfib(n - 2) + fibfib(n - 3)
    elif n < 0:
        memo[n] = fibfib(n + 3) - fibfib(n + 2) - fibfib(n + 1)
    return memo[n]
