import string

def duplicate_string_filter(s, include_nums=True, min_word_length=0):
    valid_chars = string.ascii_letters
    if include_nums:
        valid_chars += string.digits
    new_str = ''
    s_split = s.split()
    for word in s_split:
        if len(word) >= min_word_length:
            temp_str = ''
            for char in word:
                if char in valid_chars:
                    temp_str += char
            new_str += temp_str
    return new_str * 2

