import collections
import re

def analyze_text(text):
    cleaned_text = re.sub('', '', text.lower())
    words = cleaned_text.split()
    word_counts = collections.Counter(words)
    anagrams = {}
    palindromes = set()
    for word in words:
        sorted_word_tuple = tuple(sorted(word))
        if sorted_word_tuple in anagrams:
            anagrams[sorted_word_tuple].add(word)
        else:
            anagrams[sorted_word_tuple] = {word}
        if word == word[::-1] and len(word) > 1:
            palindromes.add(word)
    results = {'word_count': dict(word_counts), 'anagrams': {k: len(v) - 1 for (k, v) in anagrams.items() if len(v) > 1}, 'palindromes': list(palindromes)}
    return results

