def binomial_coefficient(n, k):
    """"""
    n_factorial = 1
    k_factorial = 1
    n_k_factorial = 1
    for i in range(1, n + 1):
        n_factorial *= i
    for i in range(1, k + 1):
        k_factorial *= i
    for i in range(1, n - k + 1):
        n_k_factorial *= i
    result = n_factorial // (k_factorial * n_k_factorial)
    return result
