from collections import Counter

def weighted_mode(lst):
    freqs = Counter(lst)
    weights = {}
    for (num, freq) in freqs.items():
        weight = num / freq
        weights[round(num, 3)] = round(weight, 3)
    mode = max(weights, key=weights.get)
    return mode

