def message_ordering(messages):
    """"""
    log = []
    sequence_numbers = {}
    for message in messages:
        (pid, sequence_number, _) = message
        if pid not in sequence_numbers:
            sequence_numbers[pid] = sequence_number
        elif sequence_numbers[pid] + 1 != sequence_number:
            continue
        log.append(message)
        sequence_numbers[pid] = sequence_number
    return log
