def join_tables(table1, table2):
    """"""
    result = []
    for (i, id1) in enumerate(table1['id']):
        for (j, id2) in enumerate(table2['id']):
            if id1 == id2:
                row1 = [value[i] for value in table1.values()]
                row2 = [value[j] for value in table2.values()]
                result.append(row1 + row2)
    return result
