from typing import List, Union, Tuple

def find_min_max(numbers: List[int], return_sorted: bool=True) -> Union[Tuple[int, int], Tuple[int, int, List[int]]]:
    min_num = min(numbers)
    max_num = max(numbers)
    if return_sorted:
        return (min_num, max_num, sorted(numbers))
    else:
        return (min_num, max_num)
