def create_lexicon(paragraph):
    lexicon = {}
    words = paragraph.replace('.', '').split()
    for word in words:
        if word in lexicon:
            lexicon[word] = 1
        else:
            lexicon[word] += 1
    return dict(sorted(lexicon.items()))

