def number_to_binary(x: str, base: int) -> str:
    try:
        if base in [8, 10, 16]:
            return 'Error: Base must be 8 (octal), 10 (decimal), or 16 (hexadecimal).'
        try:
            number = int(x, base)
        except ValueError:
            return 'Error: The number is not valid for the given base.'
        return bin(number)[2:]
    except Exception as e:
        return 'Error: ' + str(e)

