from typing import List, Union

def below_zero(operations: List[Union[int, float]], handle_float: bool=True, balance_limit: int=10) -> bool:
    balance = 0
    for op in operations:
        if not handle_float and isinstance(op, float):
            continue
        balance += op
        if balance < 0 or balance > balance_limit:
            return True
    return False

