def truncate_string(string):
    unique_chars = []
    for ch in string:
        if ch not in unique_chars:
            unique_chars.append(ch)
        if len(unique_chars) == 4:
            break
    if len(unique_chars) != 4:
        return ''.join(unique_chars)
    else:
        return ''.join(sorted(unique_chars))

