def classify_countries(country_capitals):
    """"""
    country_info = {'China': ('Asia', 'Mandarin'), 'Finland': ('Europe', 'Finnish and Swedish'), 'United States': ('America', 'English'), 'Vietnam': ('Asia', 'Vietnamese'), 'India': ('Asia', 'Hindi and English'), 'Italy': ('Europe', 'Italian'), 'Mexico': ('America', 'Spanish'), 'Russia': ('Europe/Asia', 'Russian'), 'France': ('Europe', 'French'), 'Brazil': ('America', 'Portuguese'), 'Australia': ('Oceania', 'English'), 'Egypt': ('Africa', 'Arabic'), 'South Africa': ('Africa', 'English, Afrikaans, Zulu, Xhosa, Swazi, Ndebele, Northern Sotho, Southern Sotho, Tswana, Tsonga, Venda'), 'Japan': ('Asia', 'Japanese'), 'Canada': ('America', 'English and French'), 'Argentina': ('America', 'Spanish'), 'Nigeria': ('Africa', 'English'), 'New Zealand': ('Oceania', 'English and Maori'), 'Germany': ('Europe', 'German'), 'Saudi Arabia': ('Asia', 'Arabic')}
    results = []
    for (country, capital) in country_capitals:
        (continent, language) = country_info[country]
        results.append((country, capital, continent, language))
    return results
