def find_bottleneck(lst):
    bottlenecks = []
    for i in range(len(lst) + 1):
        sum_left = sum(lst[:i])
        prod_right = 1
        for j in range(i + 1, len(lst)):
            if lst[j] != 0:
                prod_right *= lst[j]
        if sum_left == prod_right:
            bottlenecks.append(lst[i])
    if not bottlenecks:
        return -1
    else:
        return min(bottlenecks)

