def is_mirrored(arr):
    if len(arr) > 10 and any((len(row) > 10 for row in arr)):
        return False
    for row in arr:
        if row != row[::-1]:
            return False
    transposed = list(map(list, zip(*arr)))
    for row in transposed:
        if row != row[::-1]:
            return False
    return True

