def monotonic_advanced(l: list, strict: bool=True, zero_crossing: bool=False, progression: str=None):
    if len(l) < 2:
        return True
    if progression == 'arithmetic':
        diff = l[1] - l[0]
        for i in range(2, len(l)):
            if l[i] - l[i - 1] != diff:
                return False
    elif progression == 'geometric':
        if l[0] != 0:
            ratio = l[1] / l[0]
            for i in range(2, len(l)):
                if l[i - 1] != 0 and l[i] / l[i - 1] != ratio:
                    return False
        else:
            return False
    if strict:
        for i in range(1, len(l)):
            if l[i] == l[i - 1]:
                return False
    if zero_crossing:
        crossing = False
        for i in range(1, len(l)):
            if l[i] * l[i - 1] < 0:
                if crossing:
                    return False
                crossing = True
    increase = False
    decrease = False
    for i in range(1, len(l)):
        if l[i] > l[i - 1]:
            increase = True
        elif l[i] < l[i - 1]:
            decrease = True
        if increase and decrease:
            return False
    return True

