def capitalize_sentence(sentence):
    words = sentence.split()
    capitalized_words = []
    for word in words:
        if word.lower() in ['and', 'the']:
            word = word.rstrip(",.!?;:'")
            word = word[0].upper() + word[1:]
        capitalized_words.append(word)
    return ' '.join(capitalized_words)

