def flip_case_and_modify(string: str) -> str:
    result = []
    for char in string:
        if char.isdigit():
            if int(char) % 2 == 0:
                result.append(str(int(char) + 1))
            else:
                result.append(char)
        elif char.isalpha():
            if char.islower():
                result.append(char.upper())
            else:
                result.append(char.lower())
        else:
            result.append(char * 2)
    return ''.join(result)

