import collections
import heapq

def leastInterval(tasks, n):
    (curr_time, h) = (1, [])
    for (k, v) in collections.Counter(tasks).items():
        heapq.heappush(h, (-1 * v, k))
    while h:
        (i, temp) = (0, [])
        while i <= n:
            curr_time += 1
            if h:
                (x, y) = heapq.heappop(h)
                if x != -1:
                    temp.append((x + 1, y))
            if not h and (not temp):
                break
            else:
                i += 1
        for item in temp:
            heapq.heappush(h, item)
    return curr_time

